package com.ipartek.formacion.basicos.iniciales;

/**
 * Programa Java que declare una variable B de tipo entero y asígnale un valor.
 * A continuacion muestra un mensaje indicando si el valor de B es positivo o
 * negativo. Consideraremos el 0 como positivo. Utiliza el operador condicional
 * (? : ) dentro del println para resolverlo.
 * 
 * @author Curso
 *
 */

public class Ejercicio6 {

	static int b = -10;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(b < 0 ? "El número es negativo." : "El número es positivo");

	}

}
